function validateEmail(a){return/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(a)}function validateMail(){const a=$("#reqmail"),s=$("#mail").val();a.css("display","none"),validateEmail(s)&&a.css("display","block")}function validatePassword(){const a=$(".requiredPass"),s=$("#pass1").val(),i=$("#pass2").val();a.css("display","none"),s.length>3&&s==i&&a.css("display","block")}function validateName(){const a=$("#reqname"),s=$("#name").val();a.css("display","none"),s.length>5&&a.css("display","block")}$("#mail").on("input",validateMail),$("#pass2").on("input",validatePassword),$("#pass1").on("input",validatePassword),$("#name").on("input",validateName);